schema:
{
  "type": "object",
  "title": "title",
  "properties": {
    "游戏来源": {
      "type": "string",
      "enum": [
        "steam",
        "epic",
        "盗版游戏",
        "手机游戏",
        "其他"
      ]
    },
    "游戏外部分类": {
      "type": "string",
      "enum": [
        "单机游戏",
        "联机游戏",
        "网页游戏",
        "手机游戏"
      ]
    },
    "游戏内部分类": {
      "type": "string",
      "enum": [
        "枪战游戏",
        "解谜游戏",
        "剧情游戏",
        "格斗游戏",
        "玩法特殊",
        "galgame",
				"肉鸽游戏",
        "开放世界",
        "沙盒游戏",
        "视觉小说"
      ]
    },
    "金额": {
      "type": "number",
      "default": "0"
    },
    "游戏时长": {
      "type": "string"
    },
    "游玩状态": {
      "type": "string",
      "enum": [
        "玩得还行",
        "一直在玩",
        "不玩了",
        "没玩下去",
        "偶尔玩玩",
        "没修改器不玩"
      ]
    },
    "游戏简单评价": {
      "type": "string",
      "enum": [
        "经典之作",
        "有好有坏",
        "time killer",
        "3A大作",
        "MOD多",
        "一般",
        "操作太难",
        "过于简单",
        "体验不好"
      ]
    },
    "有无修改器": {
      "type": "string",
      "enum": [
        "有但不常用",
        "有且经常用",
        "无但很想要",
        "无但无所谓"
      ]
    },
    "购买时间": {
      "type": "string",
      "format": "date"
    }
  },
  "required": [
    "游戏来源",
    "游戏外部分类",
    "游戏内部分类",
    "金额",
    "游戏时长",
    "游玩状态",
    "游戏简单评价",
    "有无修改器",
    "购买时间"
  ]
}
